package corejava;

public class datatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// boolean 
		
		boolean b = true;
		System.out.println(b);
		
		// char
		
		char c = 'h';
		System.out.println(c);
		
		//byte
		
		byte b1 = 67;
		System.out.println(b1);
		
		// string
		
		String s1 = "New Delhi";
		System.out.println(s1);
		
		// short
		
		short s2 = -23456;
		System.out.println(s2);
		

	}

}
